mapper.select:
SELECT COUNT(*) AS COUNT 
FROM TBL_MANAGER 
WHERE MNG_ID = #{arg0}

SELECT MNG_CD, MNG_ID, MNG_PW, MNG_NM, MNG_NICK, MNG_TEL
FROM TBL_MANAGER
WHERE MNG_ID = #{arg0}

mapper.update:
UPDATE TBL_MANAGER
SET MNG_ID = #{mng_id}, MNG_PW = #{mng_pw}, MNG_NM = #{mng_nm}
			, MNG_NICK=#{mng_nick}, MNG_TEL=#{mng_tel}
WHERE MNG_ID = #{mng_id}
