mapper.update:
UPDATE TBL_USERINFO
SET USER_NICK = #{user_Nick}, USER_TEL=#{user_Tel}, USER_PW=#{user_Pw}
WHERE USER_ID = #{user_Id}

UPDATE TBL_USERINFO
SET USER_IMG = #{arg1}
WHERE USER_ID = #{arg0}

mapper.select:
SELECT COUNT(*)
FROM VIEW_USER_PLIST
WHERE USER_CD=#{user_cd}

SELECT POINT
FROM VIEW_MEMBERPOINT
WHERE USER_CD = #{arg0}

SELECT COUNT(*)
FROM VIEW_USER_CLIST
WHERE USER_CD=#{user_cd}

SELECT COUNT(*)
FROM VIEW_LIKE_COUNT
WHERE USER_ID=#{arg0}

SELECT COUNT(*)
FROM TBL_JJIMS
WHERE USER_CD=#{arg0}
