mapper.select:
SELECT COUNT(*) AS COUNT 
FROM TBL_USERINFO 
WHERE USER_ID = #{user_Id}

SELECT USER_NICK
FROM TBL_USERINFO 
WHERE USER_ID = #{arg0} AND USER_PW = #{arg1}

SELECT USER_CD
FROM TBL_USERINFO
WHERE USER_ID=#{arg0}

SELECT MNG_NICK
FROM TBL_MANAGER 
WHERE MNG_ID = #{arg0} AND MNG_PW = #{arg1}

SELECT USER_ID
FROM TBL_USERINFO
WHERE USER_MAIL = #{arg0}

SELECT COUNT(*)
FROM TBL_USERINFO
WHERE USER_ID=#{arg0} AND USER_MAIL=#{arg1}

mapper.update:
UPDATE TBL_USERINFO
SET USER_PW=#{arg0}
WHERE USER_ID=#{arg1}
